mapper.select:
SELECT m.*
FROM Comment m
WHERE postId = #{postId}

SELECT m.*
FROM Comment m
WHERE m.id = #{commentId}

mapper.update:
UPDATE Comment SET
  pstReactCount = #{pstReactCount} + 1
WHERE id = #{id}

UPDATE Comment SET
  pstReactCount = #{pstReactCount} - 1
WHERE id = #{id}

UPDATE Comment SET
  ngtReactCount = #{ngtReactCount} + 1
WHERE id = #{id}

UPDATE Comment SET
  ngtReactCount = #{ngtReactCount} - 1
WHERE id = #{id}
